//----------------------------
// Reading certain packet of 64 bytes from PROM to Buffer_PM in RAM
// with numbering of packets 0-50
unsigned char Frame_Buf_Read_PM(unsigned int num_pak)      
{
 const unsigned char *adres;
	unsigned char i,work;
	adres=Adr_Buf_Var+num_pak*64;
	for (i=0;i<64;i++)
		{
		work=*(adres+i);
		Buffer_PM[i]=work;
		}
}
//------------------------------
/* Initiate a write to memory */
void zap(void)
{
	GIE=0;
	WREN=1;
	EECON2=0x55;
	EECON2=0xAA;
	WR=1;
	NOP();
	while(WR);
	WREN=0;
	GIE=1;
}
//-------------------------------
// Writing certain packet of 64 bytes from Buffer_PM in RAM  to PROM 
// with numbering of packets 0-50
unsigned char  Frame_Buf_Write_PM(unsigned int num_pak) 
{     
unsigned char i_cnt;
unsigned int adres_write;
	if(num_pak>MAX_NUM_PMBUF) return 0;
	adres_write=Adr_Buf_Var+num_pak*64;
	if(global_switch_protect!=0xAA) return 0;
//--------------------------------
	TBLPTRU=0;
	TBLPTRL=(unsigned char)adres_write;
	TBLPTRH=(unsigned char)(adres_write>>8);
	EECON1=0x90;	// set up for flash erasure
	zap();
    asm("TBLRD*-");
	for (i_cnt=0;i_cnt<64;i_cnt++)
	{
		TABLAT=Buffer_PM[i_cnt];
		asm("TBLWT+*");
	}
	EECON1=0x80;	// set up for flash write
	zap();
return 1;
}



